import React, { useState } from "react";
import { Button, Form, FormField, Input } from "semantic-ui-react";
import Rating from "@mui/material/Rating";

const BookForm = () => {
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");
  const [genre, setGenre] = useState("");
  const [publishDate, setPublishDate] = useState(2000);
  const [rating, setRating] = useState(1);

  return (
    <Form>
      <Form.Field>
        <Input
          placeholder="Book Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </Form.Field>
      <Form.Field>
        <Input
          placeholder="Author"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
      </Form.Field>
      <Form.Field>
        <Input
          placeholder="Genre"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        />
      </Form.Field>
      <Form.Field>
        <Input
          placeholder="Original Publish Date"
          value={publishDate}
          onChange={(e) => setPublishDate(e.target.value)}
        />
      </Form.Field>
      <Form.Field>
        <Rating
          name="simple-controlled"
          onChange={(data) => {
            setRating(data);
          }}
          value={rating}
        />
      </Form.Field>
      <FormField>
        <Button
          onClick={async () => {
            const book = { name, author, genre, publishDate, rating };
            const response = await fetch("/add_book", {
              method: "POST",
              Headers: { "Content-Type": "application/json" },
              body: JSON.stringify(book),
            });

            if (response.ok) {
              console.log("Response Worked");
            }
          }}
        >
          Submit
        </Button>
      </FormField>
    </Form>
  );
};

export default BookForm;
